import { useEffect, useState } from "react";
import "./FoodCart.css";
import { useSelector } from "react-redux/es/hooks/useSelector";
import { useDispatch } from "react-redux";
import { fetchData } from "../app/foodslice";
import { Enums } from "../app/foodslice";
import { addInsideCart, removeInsideCart } from "../app/foodslice";

const FoodCart = ({ icon }) => {
  const dispatch = useDispatch();

  const [storeQuantity, setStoreQuantity] = useState();
  const {
    data: foodItem,
    status,
    cart_data,
  } = useSelector((state) => state.food);

  const { info } = useSelector((state) => state.footer);

  const Checked = (e) => {
    const { name, value } = e.target;
    setStoreQuantity(value);
  };
  useEffect(() => {
    dispatch(fetchData());
  }, []);

  const handleAdd = (item) => {
    console.log(storeQuantity);
    console.log(typeof item);

    item.quantity = storeQuantity;
    dispatch(addInsideCart(item));
  };

  const handleRemove = (item) => {
    dispatch(removeInsideCart(item.id));
  };

  if (status === Enums.LOADING) return <h1>Loading.....</h1>;

  if (status === Enums.ERROR) return <h1>Error</h1>;
  return (
    <div className="res-foodcontainer">
      {icon === 1 ? (
        foodItem?.map((item) => {
          if (item.category === info) {
            return (
              <div key={item.id} className="res-foodcart">
                <div className="res-div-price">{item.price}</div>
                <div className="res-div-title">{item.title}</div>
                <div className="res-div-category">{item.category}</div>
                <div>
                  <input
                    onChange={(e) => {
                      Checked(e);
                    }}
                  />
                </div>
                <div>
                  <button
                    onClick={() => {
                      handleAdd({ item });
                    }}
                  >
                    AddToCart
                  </button>
                </div>
              </div>
            );
          }
        })
      ) : (
        <p>
          {cart_data?.map((item) => {
            // if (item.id === 1) {
            return (
              <div key={item.id} className="res-foodcart">
                <div className="res-div-price">{item.price}</div>
                <div className="res-div-title">{item.title}</div>
                <div className="res-div-category">{item.category}</div>
                <div>
                  <button
                    onClick={() => {
                      handleRemove(item);
                    }}
                  >
                    RemoveCart
                  </button>
                </div>
              </div>
            );
          })}
        </p>
      )}
    </div>
  );
};

export default FoodCart;
