    console.log(res);
  };

  return (
    <div>
      {
        <tr>
          <th>
            <span className="kitchen-head-section"> </span>
          </th>
          <th>
            <span className="kitchen-head-section">Order</span>
          </th>
          <th>
            <span className="kitchen-head-quantity">Quantity</span>
          </th>
        </tr>
      }
      {display.map((item) => {
        return (
          <>
            <table>
              <tr>
                <td>
                  <span style={{ padding: "50px" }}>{item.name}</span>
                </td>
                <td>
                  <span style={{ padding: "20px" }}>{item.quantity}</span>
                </td>
                <td>
                  <button
                    className="kitchen-button-submit"
                    type="submit"
                    onClick={() => {
                      deleteFromDataBase(item._id);
                    }}
                  >
                    Deliver
                  </button>
                </td>
              </tr>
            </table>
          </>
        );
      })}
    </div>
  );
};

export default KitchenService;
